*{margin:0;padding:0;box-sizing:border-box}:root{font-family:Inter,-apple-system,BlinkMacSystemFont,Segoe UI,Roboto,Oxygen,Ubuntu,sans-serif;font-synthesis:none;text-rendering:optimizeLegibility;-webkit-font-smoothing:antialiased;-moz-osx-font-smoothing:grayscale;--bg-primary: #0a0a0a;--bg-secondary: #111111;--text-primary: #ffffff;--text-secondary: #a0a0a0;--accent-primary: #646cff;--accent-secondary: #535bf2}html{scroll-behavior:smooth}body{margin:0;padding:0;background:linear-gradient(135deg,#0a0a0a,#1a1a2e);color:var(--text-primary);overflow-x:hidden}#app{width:100%;margin:0;padding:0}.hero-section{position:relative;width:100%;min-height:100vh;display:flex;align-items:center;justify-content:center;overflow:hidden;background:radial-gradient(ellipse at center,rgba(100,108,255,.08) 0%,transparent 70%)}.hero-content{position:relative;z-index:10;text-align:center;padding:2rem;width:100%;max-width:1400px}.hero-title{font-size:clamp(2.5rem,8vw,6rem);font-weight:800;letter-spacing:clamp(.5rem,2vw,2rem);margin-bottom:4rem;line-height:1.2;opacity:0;will-change:transform,opacity}.hero-title .letter{display:inline-block;opacity:0;transform:translateY(30px);will-change:transform,opacity}.hero-title .letter.space{width:clamp(1rem,3vw,3rem)}.stats-container{display:flex;justify-content:center;gap:clamp(2rem,5vw,6rem);margin-bottom:5rem;flex-wrap:wrap;opacity:0;will-change:transform,opacity}.stat-item{text-align:center;opacity:0;transform:translateY(20px);will-change:transform,opacity}.stat-number{font-size:clamp(2rem,4vw,3.5rem);font-weight:700;background:linear-gradient(135deg,var(--accent-primary),var(--accent-secondary));-webkit-background-clip:text;-webkit-text-fill-color:transparent;background-clip:text;margin-bottom:.5rem}.stat-label{font-size:clamp(.875rem,1.5vw,1.125rem);color:var(--text-secondary);text-transform:uppercase;letter-spacing:.1em;font-weight:500}.main-visual{width:100%;max-width:800px;margin:0 auto;padding:2rem 0;opacity:0;will-change:transform,opacity}.animated-shape{width:100%;height:auto;filter:drop-shadow(0 0 30px rgba(100,108,255,.4));will-change:transform}.scroll-indicator{position:absolute;bottom:3rem;left:50%;transform:translate(-50%);display:flex;flex-direction:column;align-items:center;gap:.5rem;color:var(--text-secondary);font-size:.875rem;text-transform:uppercase;letter-spacing:.2em;opacity:0;animation:fadeInOut 2s ease-in-out infinite}.scroll-line{width:1px;height:40px;background:linear-gradient(to bottom,transparent,var(--accent-primary));animation:scrollDown 2s ease-in-out infinite}@keyframes scrollDown{0%,to{transform:translateY(-10px);opacity:0}50%{transform:translateY(10px);opacity:1}}@keyframes fadeInOut{0%,to{opacity:.3}50%{opacity:1}}.content-section{position:relative;padding:8rem 2rem;background:linear-gradient(180deg,transparent 0%,rgba(17,17,17,.9) 20%,rgba(17,17,17,.9) 80%,transparent 100%)}.content-wrapper{max-width:1200px;margin:0 auto;text-align:center}.section-title{font-size:clamp(2rem,5vw,4rem);font-weight:700;margin-bottom:2rem;background:linear-gradient(135deg,#ffffff,var(--text-secondary));-webkit-background-clip:text;-webkit-text-fill-color:transparent;background-clip:text}.section-text{font-size:clamp(1rem,2vw,1.25rem);color:var(--text-secondary);max-width:700px;margin:0 auto 4rem;line-height:1.8}.feature-grid{display:grid;grid-template-columns:repeat(auto-fit,minmax(280px,1fr));gap:2rem;margin-top:4rem}.feature-card{padding:3rem 2rem;background:#ffffff08;border:1px solid rgba(255,255,255,.05);border-radius:16px;transition:all .3s ease;will-change:transform}.feature-card:hover{transform:translateY(-8px);background:#ffffff0d;border-color:#646cff4d;box-shadow:0 20px 40px #646cff1a}.feature-icon{font-size:3rem;margin-bottom:1.5rem;filter:grayscale(.3)}.feature-card h3{font-size:1.5rem;font-weight:600;margin-bottom:1rem;color:var(--text-primary)}.feature-card p{font-size:1rem;color:var(--text-secondary);line-height:1.6}.parallax-section{position:relative;height:100vh;display:flex;align-items:center;justify-content:center;overflow:hidden}.parallax-layer{position:absolute;width:100%;height:100%;will-change:transform}.parallax-layer.layer-1{background:radial-gradient(circle at 20% 50%,rgba(100,108,255,.1) 0%,transparent 50%)}.parallax-layer.layer-2{background:radial-gradient(circle at 80% 30%,rgba(83,91,242,.08) 0%,transparent 50%)}.parallax-layer.layer-3{background:radial-gradient(circle at 50% 80%,rgba(100,108,255,.06) 0%,transparent 50%)}.parallax-content{position:relative;z-index:10;text-align:center;padding:2rem}.parallax-content h2{font-size:clamp(2.5rem,6vw,5rem);font-weight:700;margin-bottom:1.5rem;background:linear-gradient(135deg,var(--accent-primary),var(--accent-secondary));-webkit-background-clip:text;-webkit-text-fill-color:transparent;background-clip:text}.parallax-content p{font-size:clamp(1rem,2vw,1.5rem);color:var(--text-secondary)}.footer{padding:4rem 2rem;text-align:center;background:#0a0a0af2;border-top:1px solid rgba(255,255,255,.05)}.footer p{color:var(--text-secondary);font-size:.875rem;letter-spacing:.05em}@media (max-width: 768px){.hero-title{letter-spacing:clamp(.3rem,1.5vw,1rem)}.stats-container{flex-direction:column;gap:2rem}.scroll-indicator{bottom:2rem}.content-section{padding:4rem 1.5rem}.feature-grid{grid-template-columns:1fr}}@media (max-width: 480px){.hero-title{font-size:2rem;letter-spacing:.3rem}.main-visual{padding:1rem 0}}
